// Helper functions for pointer creation
func stringPtr(s string) *string { return &s }
func intPtr(i int) *int          { return &i }

// GetActionsData returns the loaded actions data.
func GetActionsData() *ActionsChummer {
	return actionsData
}

// GetAllActions returns the list of all actions.
func GetAllActions() []Action {
	return actionsData.Actions.Action
}

// GetActionByID returns the action with the given ID, or nil if not found.
func GetActionByID(id string) *Action {
	actions := GetAllActions()
	for i := range actions {
		if actions[i].ID == id {
			return &actions[i]
		}
	}
	return nil
}

// GetActionsByType returns all actions of the given type.
func GetActionsByType(actionType string) []Action {
	actions := GetAllActions()
	result := make([]Action, 0)
	for i := range actions {
		if actions[i].Type == actionType {
			result = append(result, actions[i])
		}
	}
	return result
}

// GetActionsBySource returns all actions from the given source book.
func GetActionsBySource(source string) []Action {
	actions := GetAllActions()
	result := make([]Action, 0)
	for i := range actions {
		if actions[i].Source == source {
			result = append(result, actions[i])
		}
	}
	return result
}
