// Helper functions for pointer creation
// stringPtr and intPtr are defined in actions_data.go

// GetBooksData returns the loaded books data.
func GetBooksData() *BooksChummer {
	return booksData
}

// GetAllBooks returns the list of all books.
func GetAllBooks() []Book {
	if len(booksData.Books) == 0 {
		return []Book{}
	}
	return booksData.Books[0].Book
}

// GetBookByID returns the book with the given ID, or nil if not found.
func GetBookByID(id string) *Book {
	books := GetAllBooks()
	for i := range books {
		if books[i].ID == id {
			return &books[i]
		}
	}
	return nil
}

// GetBookByCode returns the book with the given code, or nil if not found.
func GetBookByCode(code string) *Book {
	books := GetAllBooks()
	for i := range books {
		if books[i].Code == code {
			return &books[i]
		}
	}
	return nil
}
